INSERT INTO `creature_text` VALUES ('21410', '1', '0', 'My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you.', '12', '0', '100', '1', '0', '0', '0', '0', 'Zarath');
INSERT INTO `creature_text` VALUES ('21410', '2', '0', 'As you wish, my lord.', '12', '0', '100', '66', '0', '0', '0', '0', 'Zarath');
INSERT INTO `creature_text` VALUES ('21409', '0', '0', 'Halt!', '12', '0', '100', '1', '0', '0', '19511', '0', 'Icarius');
