INSERT INTO `creature_text` VALUES ('21409', '3', '0', 'Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point.', '12', '0', '100', '1', '0', '0', '0', '0', 'Icarius');
