INSERT INTO `creature_text` VALUES ('30409', '0', '0', 'You\'ve come for the doctor\'s plans! You\'ll only find death!', '12', '0', '0', '0', '0', '0', '0', '0', 'Apprentice Osterkilgr - Say 0');
INSERT INTO `creature_text` VALUES ('30409', '1', '0', 'The doctor entrusted me with the plans to Nergeld, our flesh giant amalgamation made entirely of vargul! It will be the most powerful creation of its kind and a whole legion of them will be created to destroy your pitiful forces!', '12', '0', '0', '0', '0', '0', '0', '0', 'Apprentice Osterkilgr - Say 1');
INSERT INTO `creature_text` VALUES ('25729', '0', '0', 'This is it, $N. The start of the end of the world. And you\'re here to see it off...', '12', '1', '100', '1', '0', '0', '0', '0', 'Shadowstalker Getry');
INSERT INTO `creature_text` VALUES ('25729', '1', '0', 'Let\'s go...', '12', '1', '100', '0', '0', '0', '0', '0', 'Shadowstalker Getry');
INSERT INTO `creature_text` VALUES ('25729', '2', '0', 'I... I can\'t believe it... Saurfang... I... I am honored... honored to have fought along side you, sir.', '12', '1', '100', '0', '0', '0', '0', '0', 'Shadowstalker Getry');
INSERT INTO `creature_text` VALUES ('25729', '3', '0', 'You should return to Warsong Hold now, friend. Hellscream will surely want to hear of what just happened here... of Saurfang...', '12', '1', '100', '0', '0', '0', '0', '0', 'Shadowstalker Getry');
INSERT INTO `creature_text` VALUES ('25618', '0', '0', 'Is this it? Is this all the mighty Horde could muster?', '12', '0', '100', '6', '0', '0', '0', '0', 'Varidus the Flenser');
